  function get_captcha_play(){
             $.ajax({
                url: 'https://imiwinshop.com/web/captcha/',
                type: 'GET',
                dataType: 'json',
            })
            .done(function(data) {
                //console.log(data);
                if (data.code == 1) {
                   var img = JSON.stringify(data.data);
                    $('#captcha_img_testplay').html(JSON.parse(img).image);
                    $('#captcha_img_testplay_mo').html(JSON.parse(img).image);
                    $('#captcha_img_testplay_ipad').html(JSON.parse(img).image);
                   
                }else{
                    $('#captcha_img_testplay').html('<h3 style="color:red">'+data.msg+'<h3>');
                    $('#captcha_img_testplay_mo').html('<h3 style="color:red">'+data.msg+'<h3>');
                    $('#captcha_img_testplay_ipad').html('<h3 style="color:red">'+data.msg+'<h3>');
                  
                }
            })
            .fail(function() {
                console.log("error");
            });
        }


